mapper.select:


     and pcode = #{pcode}
     and name like concat('%', #{name}, '%')
     and proc_name like concat('%', #{procName}, '%')
    and is_valid = 1
     and attribute1 = #{attribute1}
     and attribute2 = #{attribute2}
     and attribute3 = #{attribute3}

    ${params.dataScope}

order by t.create_time desc

mapper.update:
update bs_pqc_main
pcode = #{pcode}, name = #{name}, proc_name = #{procName}, remark = #{remark}, is_valid = #{isValid}, create_by = #{createBy}, create_time = #{createTime}, update_by = #{updateBy}, update_time = sysdate(), attribute1 = #{attribute1}, attribute2 = #{attribute2}, attribute3 = #{attribute3},
where id = #{id}
