mapper.select:


     and wcode like concat('%', #{wcode}, '%')
     and orderno like concat('%', #{orderno}, '%')
     and pcode like concat('%', #{pcode}, '%')
     and pname like concat('%', #{pname}, '%')
     and pspec = #{pspec}
     and qty = #{qty}
     and pstar_time = #{pstarTime}
     and pend_time = #{pendTime}
    and is_valid = '1' 

    ${params.dataScope}

order by t.create_time desc

mapper.update:
update pp_wo_book_main
wcode = #{wcode}, orderno = #{orderno}, pcode = #{pcode}, pname = #{pname}, pspec = #{pspec}, qty = #{qty}, pstar_time = #{pstarTime}, pend_time = #{pendTime}, remark = #{remark}, update_by = #{updateBy}, update_time = sysdate(), attribute1 = #{attribute1}, attribute2 = #{attribute2}, attribute3 = #{attribute3},
where id = #{id}
