mapper.select:


     and wcode like concat('%', #{wcode}, '%')
     and pcode like concat('%', #{pcode}, '%')
     and pname like concat('%', #{pname}, '%')
     and pspec like concat('%', #{pspec}, '%')
     and inqty = #{inqty}
     and outqty = #{outqty}
    and is_valid = '1' 

    ${params.dataScope}

order by t.create_time desc

mapper.update:
update qc_bad_project_main
wcode = #{wcode}, pcode = #{pcode}, pname = #{pname}, pspec = #{pspec}, inqty = #{inqty}, outqty = #{outqty}, okratio = #{okratio}, remark = #{remark}, update_by = #{updateBy}, update_time = sysdate(), attribute1 = #{attribute1}, attribute2 = #{attribute2}, attribute3 = #{attribute3},
where id = #{id}
