mapper.select:


     and DATE_FORMAT(qtime ,'%Y-%m-%d') >= DATE_FORMAT(#{qStarttime} ,'%Y-%m-%d')
     and DATE_FORMAT(qtime ,'%Y-%m-%d') <= DATE_FORMAT(#{qEndtime} ,'%Y-%m-%d') 
     and qcode like concat('%', #{qcode}, '%')
     and qtime = #{qtime}
     and pcode like concat('%', #{pcode}, '%')
     and pname like concat('%', #{pname}, '%')
     and qc_result = #{qcResult}
    and is_valid = '1'

    ${params.dataScope}

order by t.create_time desc

mapper.update:
update qc_prod_check_main
qcode = #{qcode}, qtime = #{qtime}, quser = #{quser}, pcode = #{pcode}, pname = #{pname}, pspec = #{pspec}, lot = #{lot}, qc_result = #{qcResult}, handle_result = #{handleResult}, remark = #{remark}, is_valid = #{isValid}, create_by = #{createBy}, create_time = #{createTime}, update_by = #{updateBy}, update_time = sysdate(), attribute1 = #{attribute1}, attribute2 = #{attribute2}, attribute3 = #{attribute3},
where id = #{id}
